INSERT INTO jobs (job_name, job_content, location,created_at, updated_at) VALUES ('Java开发工程师（国内项目）','<p>职位要求：</p><ol class=" list-paddingleft-2" style="list-style-type: decimal;"><li><p>大学专科以上学历，计算机专业或有计算机相关经验；</p></li><li><p>2年以上Java开发项目经验，最好有银行或电商项目开发经验；</p></li><li><p>熟悉J2EE软件体系架构，熟悉Java语言和面向对象设计和开发；</p></li><li><p>掌握数据库（DB2尤佳）应用和管理经验、WebSphere部署使用经验；</p></li><li><p>沟通表达能力较好，学习能力强，反应敏捷，思路清晰，逻辑缜密，做事踏实积极，有强烈的责任心；</p></li><li><p>参与过金融类，支付类项目尤佳。 </p></li></ol>', '北京', '2016-05-20 11:04:28', '2016-05-20 11:04:28');
